create table comments (id bigint generated by default as identity, creation_date timestamp, modify_date timestamp, grade varchar(255), remark varchar(255), score integer, actual_order_id bigint, primary key (id));
create table items (id bigint generated by default as identity, creation_date timestamp, modify_date timestamp, count integer, order_id bigint, product_id bigint, primary key (id));
create table orders (order_type varchar(31) not null, id bigint generated by default as identity, creation_date timestamp, modify_date timestamp, state varchar(255), total_price double, user_id bigint, primary key (id));
create table products (id bigint generated by default as identity, creation_date timestamp, modify_date timestamp, name varchar(255), price double, stock integer, primary key (id));
create table refund_applies (id bigint generated by default as identity, creation_date timestamp, modify_date timestamp, reason varchar(255), refuse_remark varchar(255), remark varchar(255), state varchar(255), order_id bigint, primary key (id));
create table return_applies (id bigint generated by default as identity, creation_date timestamp, modify_date timestamp, reason varchar(255), refuse_remark varchar(255), remark varchar(255), state varchar(255), order_id bigint, primary key (id));
create table return_shipments (id bigint generated by default as identity, creation_date timestamp, modify_date timestamp, company varchar(255), serial varchar(255), apply_id bigint, primary key (id));
create table shipments (id bigint generated by default as identity, creation_date timestamp, modify_date timestamp, company varchar(255), serial varchar(255), order_id bigint, primary key (id));
create table users (id bigint generated by default as identity, creation_date timestamp, modify_date timestamp, password varchar(255), username varchar(255), primary key (id));
